mapper.select:
SELECT requestId, userId, authorityId, receptionDate, name, phoneNumber, email, postcode, address, detailAddress, facilityType, issueTitle, issueDetails, preferredDateTime, receptionStatus, receptionDelivery
FROM as_reception

	WHERE userId = #{userId}

UPDATE as_reception 
SET receptionDelivery = #{receptionDelivery}, receptionStatus = #{receptionStatus}
WHERE requestId = #{requestId};

		SELECT 
        COALESCE(
            (SELECT COUNT(*) FROM member WHERE authorityId = 2) -
            (SELECT COUNT(*) FROM as_reception WHERE preferredDateTime = #{preferredDateTime}),
            0
        ) AS assignedTechniciansCount;
